setTimeout(function(){ const canvas=document.getElementById("hero-block-canvas"),c=canvas.getContext("2d");let w,h;const setCanvasExtents=()=>{w=document.body.clientWidth,h=document.body.clientHeight,canvas.width=w,canvas.height=h};setCanvasExtents(),window.onresize=(()=>{setCanvasExtents()});const makeStars=t=>{const e=[];for(let n=0;n<t;n++){const t={x:1600*Math.random()-800,y:900*Math.random()-450,z:1e3*Math.random()};e.push(t)}return e};let stars=makeStars(1e4);const clear=()=>{c.fillStyle="#231833",c.fillRect(0,0,canvas.width,canvas.height)},putPixel=(t,e,n)=>{const a=255*n,s="rgb("+a+","+a+","+a+")";c.fillStyle=s,c.fillRect(t,e,1,1)},moveStars=t=>{const e=0;for(var n=0;n<e;n++){const e=stars[n];for(e.z-=t;e.z<=1;)e.z+=1e3}};let prevTime;const init=t=>{prevTime=t,requestAnimationFrame(tick)},tick=t=>{let e=t-prevTime;prevTime=t,moveStars(.1*e),c.fillStyle="#231833",c.fillRect(0,0,canvas.width,canvas.height);const n=w/2,a=h/2,s=stars.length;for(var i=0;i<s;i++){const t=stars[i],e=n+t.x/(.001*t.z),s=a+t.y/(.001*t.z);if(e<0||e>=w||s<0||s>=h)continue;const c=t.z/1e3;putPixel(e,s,1-c*c)}requestAnimationFrame(tick)};requestAnimationFrame(init); }, 2000);